import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'

const AccountsDetails = () => {

    const [bg, setBg] = useState('')
    const [profit,setProfit] =useState(0)
    useEffect(() => {
        setProfit(100)
        if (profit > 0){

            setBg('bg-green-600')
        } else{

            setBg('bg-red-500')
        }
    })
    return (
        <div>
            <div className='w-3/4 mx-auto mt-16 flex justify-center'>
                <div className='mx-5  p-1 shadow shadow-md rounded-lg shadow-slate-500 bg-green-600 w-60'>
                    <p className='my-3 font-bold  bg-slate-100 w-fit mx-auto px-4 rounded-md text-slate-700' >Income </p>
                    <p className='my-3 font-bold font-mono mx-8 text-slate-100 ' >12000/- </p>
                </div>
                <div className='mx-5  p-1 shadow shadow-md rounded-lg shadow-slate-500 bg-red-800 w-60'>
                    <p className='my-3 font-bold  bg-slate-100 w-fit mx-auto px-4 rounded-md text-slate-700' >Expense </p>
                    <p className='my-3 font-bold font-mono mx-8 text-slate-100' >12000/- </p>
                </div>
            </div>
            <div className={`mt-5 ${bg} rounded-lg  w-1/4 py-2 mx-auto`}>
                <span className='my-3 font-bold font-mono mx-8 text-slate-100'>{profit}/-</span>
            </div>
            <div className='mt-5 w-3/4 mx-auto flex justify-center'>
                <div>
                    <textarea type="text" className='h-10 px-2 ' name="" id="" />
                    <button className='bg-white px-2 ' style={{height:''}}>Submit</button>
                </div>
            </div>
        </div>
    )
}

export default AccountsDetails
