import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'

const AccountsDetails = () => {

    const [bg, setBg] = useState('')
    const [profit, setProfit] = useState(0)
    const [expenseAmount,setExpenseAmount] = useState(0)
    const [expenseFrom,setExpenseFrom] = useState('')
    const [incomeAmount,setIncomeAmount] = useState(0)
    const [incomeFrom,setIncomeFrom] = useState('')

    useEffect(() => {
        setProfit(100)
        if (profit > 0) {

            setBg('bg-green-600')
        } else {

            setBg('bg-red-500')
        }
    },[profit])
    const Expense_Data = {
        text:expenseFrom,
        Amount:expenseAmount
    }
    const Income_Data ={
        text:incomeFrom,
        Amount:incomeAmount

    }
    const submit_income = () =>{
        setIncomeAmount(0)
        setIncomeFrom('')
        console.table(Income_Data)
    }
    const submit_expense = () =>{
        console.table(Expense_Data)
    }
    return (
        <div className='flex justify-center mt-16'>
            <div className='bg-indigo-100 rounded-lg shadow mx-8 shadow shadow-md border-8 border-white '>

                <div className='w-3/4 mx-auto mt-16 flex justify-center ' >
                    <div className='mx-5  p-1 shadow shadow-md rounded-lg shadow-slate-500 bg-green-600 w-60'>
                        <p className='my-3 font-bold  bg-slate-100 w-fit mx-auto px-4 rounded-md text-slate-700' >Income </p>
                        <p className='my-3 font-bold font-mono mx-8 text-slate-100 ' >12000/- </p>
                    </div>
                    <div className='mx-5  p-1 shadow shadow-md rounded-lg shadow-slate-500 bg-red-800 w-60'>
                        <p className='my-3 font-bold  bg-slate-100 w-fit mx-auto px-4 rounded-md text-slate-700' >Expense </p>
                        <p className='my-3 font-bold font-mono mx-8 text-slate-100' >12000/- </p>
                    </div>
                </div>
                <div className={`mt-5 ${bg} rounded-lg  w-1/4 py-2 mx-auto`}>
                    <span className='my-3 font-bold font-mono mx-8 text-slate-100'>{profit}/-</span>
                </div>
                <div className='mt-5 mb-16 w-3/4 mx-auto flex justify-center'>

                    <div className='mt-5'>
                        {/* <div className='fle'><label className='' htmlFor="">Enter Expenses</label><br /></div> */}
                        <input  onChange={(e)=>setIncomeFrom(e.target.value)} type="text" className='px-2 border-r border-slate-400 rounded-l shadow shadow-lg text-sm ' placeholder='Income From' style={{ height: '3vh' }} name="" id="" />                        
                        <input onChange={(e)=>setIncomeAmount(e.target.value)} type="number" className='px-2 shadow shadow-lg text-sm ' placeholder='Income Amount' style={{ height: '3vh' }} name="" id="" />
                        <button type='submit' onClick={submit_income} className='bg-slate-400 px-2 text-white rounded-r shadow shadow-lg text-sm' style={{ height: '3vh' }}>Submit</button> <br />

                        <input onChange={(e)=>setExpenseFrom(e.target.value)} type="text" className='px-2 border-r mt-2 border-slate-400 rounded-l shadow shadow-lg text-sm ' placeholder='Expense From' style={{ height: '3vh' }} name="" id="" />                        
                        <input onChange={(e)=>setExpenseAmount(e.target.value)} type="number" className='px-2 shadow shadow-lg text-sm ' placeholder='Expense Amount' style={{ height: '3vh' }} name="" id="" />
                        <button type='submit' onClick={submit_expense} className='bg-slate-400 px-2 text-white rounded-r shadow shadow-lg text-sm' style={{ height: '3vh' }}>Submit</button> <br />


                    </div>
                </div>
            </div>
        </div>
    )
}

export default AccountsDetails
