import moment from 'moment'
import React from 'react'
import { useState } from 'react'
import { ToastContainer,toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';


const Bookings = () => {
  const [name,setName] =useState('')
  const [address,setAddress] = useState('')
  const [number ,setNumber] = useState(0)
  const[bookingDate,setBookingDate] = useState('')

  const bookingDetails = {
    name:name,
    address:address,
    phone:number,
    booking:bookingDate,
    date: moment(Date.now()).format('ll')
  }

  const handleSubmit = () =>{
    if(name === '' || address ==='' || number === '' || bookingDate ==='') toast.error('Enter all Boo')
    console.table(bookingDetails)
}
  return (
    <div>
      <ToastContainer/>
      <div className=' justify-center max-auto pt-10'>
          <div className=' mx-auto shadow shadow-md shadow-slate-600 md:w-1/4 sm:w-1/4 lg:w-1/4 pb-5 bg-white mt-16' style={{height:'50vh'}}>
            <div className='bg-blue-600 text-lg font-bold text-white py-5 'style={{width:'auto'}}>Add Booking</div>

            <input required placeholder='Enter Name' onChange={(e)=>setName(e.target.value)} type="text" className='bg-slate-200 py-1 px-2 text-sm mt-16 w-2/3 rounded my-2 mx-2' />
            <textarea required placeholder='Enter Address' onChange={(e)=>setAddress(e.target.value)} type="text" className='bg-slate-200 h-20 py-1 px-2 text-sm w-2/3 rounded my-2 mx-2' />
            <input required placeholder='Enter Phone Number' onChange={(e)=>setNumber(e.target.value)} minLength='10' type="Number" className='bg-slate-200 py-1 px-2 text-sm w-2/3 rounded my-2 mx-2' />
            <input required placeholder='' type="date" onChange={(e)=>setBookingDate(e.target.value)} className='bg-slate-200 py-1 px-2 text-sm w-2/3 rounded my-2 mx-2' />

            <div className=''>
              <button type='submit' onClick={handleSubmit} className='mx-auto mt-5 bg-blue-500 px-5 py-1 text-white' style={{width:'17vw'}}>Add</button>
            </div>
          </div>
      </div>  
    </div>
  )
}

export default Bookings
