import React from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import {toast,ToastContainer} from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios'
const AccountsDetails = () => {

    const [bg, setBg] = useState('')
    const [profit, setProfit] = useState(0)
    const [expenseAmount,setExpenseAmount] = useState(0)
    const [expenseFrom,setExpenseFrom] = useState('')
    const [incomeAmount,setIncomeAmount] = useState(0)
    const [incomeFrom,setIncomeFrom] = useState('')
    const [alltransactions,setAllTransactions] = useState([])

    useEffect(() => {
        axios.get("http://localhost:5000/admin/transactions").then((res)=>
        {
            setAllTransactions(res.data)
        })
        setProfit(incomeAmount-expenseAmount)
        if (profit > 0) {

            setBg('bg-green-500')
        } else if(profit===0){

            setBg('bg-slate-400')
        }else{
            setBg('bg-red-400')

        }
    },[profit,incomeAmount,expenseAmount])
    const Expense_Data = {
        text:expenseFrom,
        Amount:expenseAmount
    }
    const Income_Data ={
        text:incomeFrom,
        Amount:incomeAmount

    }
    const submit_income = () =>{
        toast.success('Income Updated',{autoClose:500})
        console.table(Income_Data)
        setIncomeAmount(0)
        setIncomeFrom('')
    }
    const submit_expense = () =>{
        toast.success('Expense Updated',{autoClose:500})

        console.table(Expense_Data)
        setExpenseAmount(0)
        setExpenseFrom('')
    }
    return (
        <div className='flex justify-center mt-16'>
            <ToastContainer/>
           <div></div>


        </div>
    )
}

export default AccountsDetails
